import React, { useState, useEffect } from "react";
import "./Home.css";
import Header from "../components/Header";

// Slider Images
import slide1 from "../assets/slider1.jpg";
import slide2 from "../assets/slider2.jpg";
import slide3 from "../assets/slider3.jpg";
import slide4 from "../assets/slider4.jpg";

// Exercise Images
import med1 from "../assets/meditation1.jpg";
import med2 from "../assets/meditation2.jpg";
import med3 from "../assets/meditation3.jpg";
import med4 from "../assets/meditation4.jpg";

const exerciseVideos = {
  sleep: "https://www.youtube.com/embed/aEqlQvczMJQ",
  breathing: "https://www.youtube.com/embed/sJ04nsiz_M0",
  meditation: "https://www.youtube.com/embed/inpok4MKVLM",
  yoga: "https://www.youtube.com/embed/hJbRpHZr_d0",
  relaxmusic: "https://www.youtube.com/embed/2OEL4P1Rz04",
  bodyscan: "https://www.youtube.com/embed/QHkXvPq2pQE",
  focus: "https://www.youtube.com/embed/1vx8iUvfyCY",
  energizer: "https://www.youtube.com/embed/Q-qkXt9pPp8"
};

function Home() {
  const [videoUrl, setVideoUrl] = useState("");
  const [showVideo, setShowVideo] = useState(false);

  // Slider Logic
  const slides = [slide1, slide2, slide3, slide4];
  const [currentSlide, setCurrentSlide] = useState(0);

  useEffect(() => {
    const nextSlide = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 4000);

    return () => clearInterval(nextSlide);
  }, []);

  const openVideo = (url) => {
    setVideoUrl(url);
    setShowVideo(true);
  };

  const closeVideo = () => {
    setVideoUrl("");
    setShowVideo(false);
  };

  return (
    <>
      <Header />

      {/* IMAGE SLIDER */}
      <div className="slider-container">
        {slides.map((img, index) => (
          <div
            key={index}
            className={`slide ${index === currentSlide ? "active" : ""}`}
            style={{ backgroundImage: `url(${img})` }}
          ></div>
        ))}
      </div>

      {/* MAIN EXERCISE SECTION */}
      <section className="features-section">
        <h2>Mental Health Exercises</h2>
        <p className="sub-text">
          Improve your wellbeing with guided activities designed for stress relief and relaxation.
        </p>

        <div className="card-row">
          {/* Existing 4 */}
          <div className="feature-card" onClick={() => openVideo(exerciseVideos.sleep)}>
            <img src={med1} alt="Better Sleep" />
            <h3>Better Sleep</h3>
            <p>Guided sleep meditation for deeper rest.</p>
          </div>

          <div className="feature-card" onClick={() => openVideo(exerciseVideos.breathing)}>
            <img src={med2} alt="Breathing" />
            <h3>Deep Breathing</h3>
            <p>4-7-8 breathing to reduce anxiety.</p>
          </div>

          <div className="feature-card" onClick={() => openVideo(exerciseVideos.meditation)}>
            <img src={med3} alt="Meditation" />
            <h3>Calm Meditation</h3>
            <p>10-minute mindfulness meditation.</p>
          </div>

          <div className="feature-card" onClick={() => openVideo(exerciseVideos.yoga)}>
            <img src={med4} alt="Yoga" />
            <h3>Anxiety Relief Yoga</h3>
            <p>Gentle yoga flow to release tension.</p>
          </div>

          {/* NEW 4 */}
          <div className="feature-card" onClick={() => openVideo(exerciseVideos.relaxmusic)}>
            <img src={med2} alt="Relaxing Music" />
            <h3>Relaxing Music</h3>
            <p>Soothing sounds to calm your mind.</p>
          </div>

          <div className="feature-card" onClick={() => openVideo(exerciseVideos.bodyscan)}>
            <img src={med3} alt="Body Scan" />
            <h3>Body Scan Meditation</h3>
            <p>Release tension by scanning your body.</p>
          </div>

          <div className="feature-card" onClick={() => openVideo(exerciseVideos.focus)}>
            <img src={med1} alt="Focus" />
            <h3>Focus Meditation</h3>
            <p>Improve concentration & mental clarity.</p>
          </div>

          <div className="feature-card" onClick={() => openVideo(exerciseVideos.energizer)}>
            <img src={med4} alt="Energizer" />
            <h3>Morning Energizer</h3>
            <p>Boost energy and start your day fresh.</p>
          </div>
        </div>
      </section>

      {/* ABOUT SECTION */}
      <section className="about-section">
        <h2>About The Project</h2>
        <p>
          This platform focuses on mental health improvement using guided exercises,
          meditation practices, breathing techniques, and mindfulness routines.
        </p>

        <p>
          Our aim is to help individuals reduce stress, improve emotional resilience,
          and lead healthier lives through easy-to-follow mental wellness activities.
        </p>
      </section>

      <footer className="footer">© 2025 Mental Health Monitoring</footer>

      {/* VIDEO POPUP */}
      {showVideo && (
        <div className="video-modal">
          <div className="video-container">
            <span className="close-btn" onClick={closeVideo}>×</span>
            <iframe
              src={videoUrl}
              title="Exercise Video"
              frameBorder="0"
              allow="autoplay; encrypted-media"
              allowFullScreen
            ></iframe>
          </div>
        </div>
      )}
    </>
  );
}

export default Home;
