import React, { useState } from "react";
import "./DoctorDashboard.css";
import UploadPrescription from "./UploadPrescription";
import axios from "axios";

export default function DoctorDashboard() {
  const [active, setActive] = useState("dashboard");

  // safe parse user from localStorage
  const user = (() => {
    try {
      return JSON.parse(localStorage.getItem("user"));
    } catch {
      return null;
    }
  })();

  // Dummy patient list
  const patients = [
    { name: "Rahul Sharma", age: 24, status: "High Stress" },
    { name: "Sneha Patil", age: 29, status: "Normal" },
    { name: "Amit Verma", age: 33, status: "Low HRV" },
  ];

  // Dummy patient reports
  const dummyReports = {
    heartRate: "92 BPM",
    spo2: "97%",
    stressScore: 68,
    stressLevel: "Moderate",
    hrv: "42 ms",
  };

  // ML form state (used in AI Assistant view)
  const [formData, setFormData] = useState({
    hf_ms2: "",
    avnn_ms: "",
    mean_hr_bpm: "",
    lf_hf_ratio: "",
    sdnn_ms: "",
    lf_norm_nu: "",
    hf_norm_nu: "",
    lf_ms2: "",
    rmssd_ms: "",
    pnn50_percent: "",
    nn50_beats: ""
  });
  const [result, setResult] = useState(null);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:5000/api/ml/predict", formData);
      // adjust according to your API response shape
      setResult(res.data?.ml_result?.prediction ?? res.data);
    } catch (err) {
      console.error(err);
      alert("Prediction failed");
    }
  };

  return (
    <div className="doctor-dashboard">
      {/* SIDEBAR */}
      <aside className="sidebar">
        <h2 className="sidebar-title">Doctor Panel</h2>

        <ul className="sidebar-menu">
          <li className={active === "dashboard" ? "active" : ""} onClick={() => setActive("dashboard")}>üè† Dashboard</li>
          <li className={active === "upload" ? "active" : ""} onClick={() => setActive("upload")}>üì§ Upload Prescription</li>
          <li className={active === "patients" ? "active" : ""} onClick={() => setActive("patients")}>üë• Patient List</li>
          <li className={active === "reports" ? "active" : ""} onClick={() => setActive("reports")}>üìä Patient Reports</li>
          <li className={active === "chat" ? "active" : ""} onClick={() => setActive("chat")}>ü§ñ AI Assistant</li>
          <li className="logout-btn"><a href="/login">‚¨Ö Logout</a></li>
        </ul>
      </aside>

      {/* MAIN CONTENT */}
      <main className="dashboard-content">
        {/* DASHBOARD HOME */}
        {active === "dashboard" && (
          <div>
            <h1 className="welcome-title">Welcome, Dr. {user?.fullName ?? "User"} üëã</h1>
            <h2 className="section-title">Overview</h2>
            <p className="section-subtitle">Here is an overview of your patients' condition</p>

            <div className="card-row">
              <div className="ai-card">
                <h3>Total Patients</h3>
                <p className="big-number">{patients.length}</p>
              </div>

              <div className="ai-card">
                <h3>Critical Alerts</h3>
                <p className="big-number">2</p>
              </div>

              <div className="ai-card">
                <h3>Pending Prescriptions</h3>
                <p className="big-number">1</p>
              </div>
            </div>
          </div>
        )}

        {/* UPLOAD PRESCRIPTION */}
        {active === "upload" && <UploadPrescription />}

        {/* PATIENT LIST */}
        {active === "patients" && (
          <div>
            <h2 className="section-title">Patient List</h2>
            <table className="patient-table">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Age</th>
                  <th>Status</th>
                </tr>
              </thead>
              <tbody>
                {patients.map((p, index) => (
                  <tr key={index}>
                    <td>{p.name}</td>
                    <td>{p.age}</td>
                    <td className={p.status.includes("High") ? "red-text" : ""}>{p.status}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        {/* PATIENT REPORTS */}
        {active === "reports" && (
          <div>
            <h2 className="section-title">Patient Reports Overview</h2>
            <div className="reports-card">
              <p><strong>Heart Rate:</strong> {dummyReports.heartRate}</p>
              <p><strong>SpO‚ÇÇ:</strong> {dummyReports.spo2}</p>
              <p><strong>Stress Score:</strong> {dummyReports.stressScore}</p>
              <p><strong>Stress Level:</strong> {dummyReports.stressLevel}</p>
              <p><strong>HRV:</strong> {dummyReports.hrv}</p>
            </div>
          </div>
        )}

        {/* AI ASSISTANT / ML PREDICTION FORM */}
        {active === "chat" && (
          <div>
            <h2 className="section-title">AI Assistant</h2>
            <p>Use the form below to predict stress from HRV features.</p>

            <form className="ml-form" onSubmit={handleSubmit}>
              {Object.keys(formData).map((key) => (
                <div key={key} className="form-row">
                  <label htmlFor={key}>{key}</label>
                  <input
                    id={key}
                    name={key}
                    type="number"
                    step="any"
                    placeholder={key}
                    value={formData[key]}
                    onChange={handleChange}
                    required
                  />
                </div>
              ))}

              <button type="submit">Predict Stress</button>
            </form>

            {result !== null && (
              <div className="prediction-result">
                <h3>Stress Level: {Number(result) === 1 ? "High Stress" : "Low Stress"}</h3>
              </div>
            )}
          </div>
        )}
      </main>
    </div>
  );
}
