import React, { useState } from "react";
import "./Register.css";
import Header from "../components/Header";
import axios from "axios";

function Register() {
  const [form, setForm] = useState({
    name: "",
    email: "",
    password: "",
    role: "Patient",
    age: "",
    gender: "",
    bloodGroup: "",
  });

  const update = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const submitRegister = async (e) => {
    e.preventDefault();

    try {
      await axios.post("http://localhost:5000/api/auth/register", form);
      alert("Registration Successful!");
      window.location.href = "/login";
    } catch (err) {
      alert(err.response?.data?.message || "Registration failed");
    }
  };

  return (
    <>
      <Header />

      <div className="register-container">
        <div className="register-box">
          <h2 className="register-title">Create Account</h2>

          <form onSubmit={submitRegister}>
            <label>Email</label>
            <input name="email" onChange={update} />

            <label>Password</label>
            <input type="password" name="password" onChange={update} />

            <label>Register as</label>
            <select name="role" onChange={update}>
              <option>Patient</option>
              <option>Doctor</option>
            </select>

            <label>Full Name</label>
            <input name="name" onChange={update} />

            <label>Age</label>
            <input name="age" onChange={update} />

            <label>Gender</label>
            <select name="gender" onChange={update}>
              <option>Male</option>
              <option>Female</option>
            </select>

            <label>Blood Group</label>
            <select name="bloodGroup" onChange={update}>
              <option>B+ / O-</option>
              <option>O+</option>
              <option>A+</option>
            </select>

            <button className="register-btn" type="submit">
              Register
            </button>
          </form>

          <p className="login-link">
            Already have an account? <a href="/login">Login here</a>
          </p>
        </div>
      </div>
    </>
  );
}

export default Register;
