import React, { useState } from "react";
import "./Register.css";

const Register = () => {
  const [form, setForm] = useState({
    role: "Patient",
    email: "",
    password: "",
    fullName: "",
    age: "",
    gender: "Male",
    bloodGroup: "",
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleRegister = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch("http://localhost:5000/api/auth/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });

      const data = await res.json();

      if (!res.ok) {
        alert(data.message);
        return;
      }

      alert("Registration Successful!");
      window.location.href = "/login";
    } catch (err) {
      alert("Server error");
    }
  };

  return (
    <div className="register-container">

      <div className="register-box">
        <h2>Create Account</h2>

        <form onSubmit={handleRegister}>

          <label>Register as</label>
          <select name="role" value={form.role} onChange={handleChange}>
            <option>Patient</option>
            <option>Doctor</option>
          </select>

          <label>Full Name</label>
          <input name="fullName" type="text" value={form.fullName} onChange={handleChange} required />

          <label>Email</label>
          <input name="email" type="email" value={form.email} onChange={handleChange} required />

          <label>Password</label>
          <input name="password" type="password" value={form.password} onChange={handleChange} required />

          <label>Age</label>
          <input name="age" type="number" value={form.age} onChange={handleChange} required />

          <label>Gender</label>
          <select name="gender" value={form.gender} onChange={handleChange}>
            <option>Male</option>
            <option>Female</option>
          </select>

          <label>Blood Group</label>
          <input name="bloodGroup" type="text" value={form.bloodGroup} onChange={handleChange} required />

          <button type="submit">Register</button>

        </form>

        <p>
          Already have an account? <a href="/login">Login</a>
        </p>

      </div>

    </div>
  );
};

export default Register;
