import React, { useEffect, useState } from "react";
import "./PatientDashboard.css";
import axios from "axios";
import UploadReports from "./UploadReports";


function PatientDashboard() {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [data, setData] = useState({
    name: "",
    age: "",
    bloodGroup: "",
    condition: "",
    stressScore: 0,
    stressLevel: "",
    lastUpdated: "",
    wellnessTip: "",
    activitySuggestion: "",
    weeklyStress: [],
  });

  useEffect(() => {
    const fetchDashboard = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/patient/dashboard");
        setData(res.data);
        setLoading(false);
      } catch (err) {
        console.error(err);
        setError("Failed to load dashboard data");
        setLoading(false);
      }
    };

    fetchDashboard();
  }, []);

  if (loading) {
    return (
      <div className="patient-dashboard loading-screen">
        <p>Loading your dashboard...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="patient-dashboard loading-screen">
        <p>{error}</p>
      </div>
    );
  }

  return (
    <div className="patient-dashboard">
      {/* SIDEBAR */}
      <aside className="sidebar">
        <h2 className="sidebar-title">Patient Panel</h2>

        <ul className="sidebar-menu">
  <li className={active === "dashboard" ? "active" : ""} onClick={() => setActive("dashboard")}>üè† Dashboard</li>

  <li className={active === "upload" ? "active" : ""} onClick={() => setActive("upload")}>üì§ Upload Reports</li>

  <li className={active === "exercise" ? "active" : ""} onClick={() => setActive("exercise")}>üßò Mental Exercises</li>

  <li className={active === "chat" ? "active" : ""} onClick={() => setActive("chat")}>ü§ñ AI Assistant</li>

  <li className="logout-btn">
    <a href="/login">‚¨Ö Logout</a>
  </li>
</ul>

      </aside>

      {/* MAIN CONTENT */}
      <main className="dashboard-content">
  {active === "dashboard" && (
    <>
      ... your existing home dashboard content ...
    </>
  )}

  {active === "upload" && <UploadReports />}

  {active === "exercise" && <p>Mental Exercise list will be shown here.</p>}

  {active === "chat" && <p>AI Chat Assistant dummy area.</p>}



        {/* PROFILE CARD */}
        <div className="ai-card">
  <h3>Sensor Readings</h3>
  <p><strong>ECG Heart Rate:</strong> {data.ecgHeartRate} BPM</p>
  <p><strong>Pulse Rate:</strong> {data.pulseRate} BPM</p>
  <p><strong>Body Temperature:</strong> {data.bodyTemp} ¬∞C</p>
  <p><strong>SpO‚ÇÇ:</strong> {data.spo2} %</p>
  <p><strong>HRV:</strong> {data.hrv} ms</p>
</div>

        {/* AI SUGGESTIONS */}
        <div className="card-row">
          <div className="ai-card">
            <h3>Stress Level</h3>
            <p>
              <strong>Score:</strong> {data.stressScore} / 100
            </p>
            <p>
              <strong>Level:</strong> {data.stressLevel}
            </p>
            <p className="updated-text">Last updated: {data.lastUpdated}</p>
          </div>

          <div className="ai-card">
            <h3>Wellness Tip</h3>
            <p>{data.wellnessTip}</p>
          </div>

          <div className="ai-card">
            <h3>Suggested Activity</h3>
            <p>{data.activitySuggestion}</p>
          </div>
        </div>

        {/* WEEKLY OVERVIEW */}
        <div className="large-card">
          <h3>Weekly Stress Overview</h3>
          <p className="small-hint">
            This graph is powered by dummy data now ‚Äì will connect to ML model later.
          </p>

          <div className="weekly-graph">
            {data.weeklyStress.map((item) => (
              <div key={item.day} className="bar-item">
                <div className="bar-label">{item.day}</div>
                <div className="bar-outer">
                  <div
                    className="bar-inner"
                    style={{ height: `${item.score}%` }}
                  ></div>
                </div>
                <div className="bar-score">{item.score}</div>
              </div>
            ))}
          </div>
        </div>
      </main>
    </div>
  );
}

export default PatientDashboard;
